    // kumpulkan data jadi satu objek arraylist
    public static ArrayList<KelasDicoding> getListData(){
        ArrayList<KelasDicoding> list = new ArrayList<>();
        KelasDicoding kelas = null;
        for (int i=0; i<data.length; i++){
            kelas = new KelasDicoding();
            kelas.setNama(data[i][0]);
            kelas.setPenyusun(data[i][1]);
            kelas.setThumnailDiskripsi(data[i][2]);
            kelas.setDiskripsi(data[i][3]);
            kelas.setThumnail(data[i][4]);
            kelas.setPhotodiskripsi(data[i][5]);
            kelas.setBiaya(data[i][6]);

            list.add(kelas);
        }
        return list;
    }
